{
  "data_fim_plantio": "{{add (body 'data_inicio_plantio') (body 'planta.dias_maturidade') 'days'}}",
  "descritivo de como plantar": "Para plantar o {{body 'planta.nome_cientifico'}}, escolha um local ensolarado no seu jardim ou em um vaso. Prepare o solo, garantindo que ele esteja bem drenado e fértil. Plante as sementes a uma profundidade de 1 cm e mantenha o solo úmido, mas não encharcado. Após cerca de {{body 'planta.dias_maturidade'}} dias, você poderá colher as folhas frescas.",
  "informacoes adicionais": "O {{body 'planta.nome_cientifico'}} é uma planta aromática que se adapta bem a climas quentes. Certifique-se de regá-lo regularmente e evite deixá-lo exposto a temperaturas muito baixas. Pode ser cultivado em vasos ou diretamente no solo do jardim.",
  "tarefas": [
    {
      "nome": "Regar o {{body 'planta.nome_cientifico'}}",
      "tipo": "cuidados",
      "quantidade_total": {{body 'planta.dias_maturidade'}},
      "cron": "0 7 * * *",
      "habilidade": {
        "nome": "nome da habilidade",
        "multiplicador_xp": 1.1
      }
    },
    {
      "nome": "Adubar o {{body 'planta.nome_cientifico'}}",
      "tipo": "cuidados",
      "quantidade_total": {{divide (body 'planta.dias_maturidade') 15}},
      "cron": "0 7 * * 0",
      "habilidade": {
        "nome": "nome da habilidade",
        "multiplicador_xp": 1.2
      }
    },
    {
      "nome": "Verificar pragas no {{body 'planta.nome_cientifico'}}",
      "tipo": "cuidados",
      "quantidade_total": {{divide (body 'planta.dias_maturidade') 10}},
      "cron": "0 7 * * 3",
      "habilidade": {
        "nome": "nome da habilidade",
        "multiplicador_xp": 1.3
      }
    },
    {
      "nome": "Colher o {{body 'planta.nome_cientifico'}}",
      "tipo": "colheita",
      "quantidade_total": 1,
      "cron": null,
      "habilidade": {
        "nome": "nome da habilidade",
        "multiplicador_xp": 2.0
      }
    }
  ]
}

